CREATE PROCEDURE dbo.Insert_Invoices
AS
BEGIN
INSERT INTO [dbo].[Invoice]([REFF_NO_SK]
      ,[ORDER_REF]
      ,[INVOICE_REFERENCE]
      ,[INV_DATE]
      ,[INVOICE_STATUS]
      ,[INVOICE_HOLD_REASON]
      ,[INVOICE_AMOUNT]
      ,[INVOICE_DESCRIPTION])

SELECT [ORDER_REF]+''+[INVOICE_REFERENCE] + ''+ CONVERT(VARCHAR,ROW_NUMBER() OVER (PARTITION BY [ORDER_REF],[INVOICE_REFERENCE]   ORDER BY [ORDER_REF])) REFF_NO_SK
,* FROM (
SELECT  [ORDER_REF], [INVOICE_REFERENCE]
    -- ,REPLACE(CONVERT(VARCHAR,[INVOICE_DATE],111),'-','/') INV_DATE
	 ,CONVERT(DATE,SUBSTRING([INVOICE_DATE],7,4)+ '/'+ SUBSTRING([INVOICE_DATE],4,2)+ '/'+ SUBSTRING([INVOICE_DATE],1,2)) INV_DATE
      --,[INVOICE_DATE]
      ,[INVOICE_STATUS]
      ,[INVOICE_HOLD_REASON]
      --,[INVOICE_AMOUNT]
	  ,CONVERT(INT,REPLACE(REPLACE(REPLACE(REPLACE(REPLACE([INVOICE_AMOUNT],',',''),'o','0'),'s','5'),'S','5'),'I','1')) [INVOICE_AMOUNT]
      ,[INVOICE_DESCRIPTION] FROM [MSBI].[dbo].[XXBCM_ORDER_MGT]
