----Insert Supplier
CREATE PROCEDURE dbo.Insert_Supplier
AS
BEGIN
INSERT INTO Supplier([SUPPLIER_NAME]
      ,[SUPP_CONTACT_NAME]
      ,[SUPP_ADDRESS]
      ,[SUPP_CONTACT_NUMBER]
      ,[SUPP_EMAIL])
	  SELECT [SUPPLIER_NAME]
      ,[SUPP_CONTACT_NAME]
      ,[SUPP_ADDRESS]
      ,[SUPP_CONTACT_NUMBER]
      ,[SUPP_EMAIL] FROM (
SELECT * FROM (
SELECT ROW_NUMBER() OVER (PARTITION BY SUPPLIER_NAME ORDER BY SUPPLIER_NAME) ROWNO,
    * FROM (
	SELECT [SUPPLIER_NAME]
	,[SUPP_CONTACT_NAME]
	,LTRIM(RTRIM(REPLACE(REPLACE([SUPP_ADDRESS],'-,',''),'- ,',''))) [SUPP_ADDRESS]
	,SUBSTRING(LTRIM(RTRIM(REPLACE(REPLACE([SUPP_CONTACT_NUMBER],',',','),' ',''))),0,CHARINDEX(',',LTRIM(RTRIM(REPLACE(REPLACE([SUPP_CONTACT_NUMBER],',',','),' ',''))))) [SUPP_CONTACT_NUMBER]	
	,[SUPP_EMAIL] FROM [MSBI].[dbo].[XXBCM_ORDER_MGT]
	WHERE LTRIM(RTRIM(REPLACE(REPLACE([SUPP_CONTACT_NUMBER],',',','),' ',''))) NOT like  '%[A-Z]%' and SUBSTRING(LTRIM(RTRIM(REPLACE(REPLACE([SUPP_CONTACT_NUMBER],',',','),' ',''))),0,CHARINDEX(',',LTRIM(RTRIM(REPLACE(REPLACE([SUPP_CONTACT_NUMBER],',',','),' ',''))))) <>' '
	GROUP BY [SUPPLIER_NAME]
	,[SUPP_CONTACT_NAME]
	,LTRIM(RTRIM(REPLACE(REPLACE([SUPP_ADDRESS],'-,',''),'- ,',''))) 
	,SUBSTRING(LTRIM(RTRIM(REPLACE(REPLACE([SUPP_CONTACT_NUMBER],',',','),' ',''))),0,CHARINDEX(',',LTRIM(RTRIM(REPLACE(REPLACE([SUPP_CONTACT_NUMBER],',',','),' ','')))))
	,[SUPP_EMAIL]
	UNION ALL
	SELECT [SUPPLIER_NAME]
	,[SUPP_CONTACT_NAME]
	,LTRIM(RTRIM(REPLACE(REPLACE([SUPP_ADDRESS],'-,',''),'- ,',''))) [SUPP_ADDRESS]
	,REPLACE(REPLACE(SUBSTRING(LTRIM(RTRIM(REPLACE(REPLACE([SUPP_CONTACT_NUMBER],',',','),' ',''))),CHARINDEX(',',LTRIM(RTRIM(REPLACE(REPLACE([SUPP_CONTACT_NUMBER],',',','),' ','')))),LEN(LTRIM(RTRIM(REPLACE(REPLACE([SUPP_CONTACT_NUMBER],',',','),' ',''))))),',',''),'.','') [SUPP_CONTACT_NUMBER]
	,[SUPP_EMAIL] 
	FROM [MSBI].[dbo].[XXBCM_ORDER_MGT]
	WHERE LTRIM(RTRIM(REPLACE(REPLACE([SUPP_CONTACT_NUMBER],',',','),' ',''))) NOT like  '%[A-Z]%'  
	GROUP BY [SUPPLIER_NAME]
	,[SUPP_CONTACT_NAME]
	,LTRIM(RTRIM(REPLACE(REPLACE([SUPP_ADDRESS],'-,',''),'- ,',''))) 
	,REPLACE(REPLACE(SUBSTRING(LTRIM(RTRIM(REPLACE(REPLACE([SUPP_CONTACT_NUMBER],',',','),' ',''))),CHARINDEX(',',LTRIM(RTRIM(REPLACE(REPLACE([SUPP_CONTACT_NUMBER],',',','),' ','')))),LEN(LTRIM(RTRIM(REPLACE(REPLACE([SUPP_CONTACT_NUMBER],',',','),' ',''))))),',',''),'.','')
	,[SUPP_EMAIL]
	) A)B  ) C
    END
