--Insert orders
CREATE PROCEDURE dbo.Insert_Orders
AS
BEGIN
INSERT INTO [dbo].[Orders]([REFF_NO_SK]
      ,[ORDER_REF]
      ,[ORDER_DATE]
      ,[ORDER_TOTAL_AMOUNT]
      ,[ORDER_DESCRIPTION]
      ,[ORDER_STATUS]
      ,[ORDER_LINE_AMOUNT]
      ,[SUPPLIER_NAME])
SELECT DISTINCT REFF_NO_SK,[ORDER_REF],[ORDER_DATE],[ORDER_TOTAL_AMOUNT],[ORDER_DESCRIPTION],[ORDER_STATUS],[ORDER_LINE_AMOUNT],S.[SUPPLIER_NAME] 
 FROM (SELECT CASE WHEN LEN([ORDER_REF]+''+[INVOICE_REFERENCE] + ''+ CONVERT(VARCHAR,ROW_NUMBER() OVER (PARTITION BY [ORDER_REF],[INVOICE_REFERENCE]   ORDER BY [ORDER_REF]))) IS NULL THEN [ORDER_REF]
	 ELSE [ORDER_REF]+''+[INVOICE_REFERENCE] + ''+ CONVERT(VARCHAR,ROW_NUMBER() OVER (PARTITION BY [ORDER_REF],[INVOICE_REFERENCE]   ORDER BY [ORDER_REF])) END REFF_NO_SK
	,[ORDER_REF]
	,CONVERT(VARCHAR,CONVERT(DATE,[ORDER_DATE]),111) [ORDER_DATE]
	,CONVERT(INT,REPLACE([ORDER_TOTAL_AMOUNT],',','')) [ORDER_TOTAL_AMOUNT]
	,[ORDER_DESCRIPTION]
	,[ORDER_STATUS]
	,[SUPPLIER_NAME]
	,CONVERT(INT,REPLACE(REPLACE(REPLACE(REPLACE(REPLACE([ORDER_LINE_AMOUNT],',',''),'o','0'),'s','5'),'S','5'),'I','1'))  [ORDER_LINE_AMOUNT] 
	FROM [MSBI].[dbo].[XXBCM_ORDER_MGT]
	WHERE [ORDER_DATE]  LIKE '%[A-Z]%'	
	UNION ALL
	SELECT CASE WHEN LEN([ORDER_REF]+''+[INVOICE_REFERENCE] + ''+ CONVERT(VARCHAR,ROW_NUMBER() OVER (PARTITION BY [ORDER_REF],[INVOICE_REFERENCE]   ORDER BY [ORDER_REF]))) IS NULL THEN [ORDER_REF]
	ELSE [ORDER_REF]+''+[INVOICE_REFERENCE] + ''+ CONVERT(VARCHAR,ROW_NUMBER() OVER (PARTITION BY [ORDER_REF],[INVOICE_REFERENCE]   ORDER BY [ORDER_REF])) END REFF_NO_SK
	,[ORDER_REF]
	,SUBSTRING([ORDER_DATE],7,4)+ '/'+ SUBSTRING([ORDER_DATE],4,2)+ '/'+ SUBSTRING([ORDER_DATE],1,2) [ORDER_DATE]
	,CONVERT(INT,REPLACE([ORDER_TOTAL_AMOUNT],',','')) [ORDER_TOTAL_AMOUNT]
	,[ORDER_DESCRIPTION]
	,[ORDER_STATUS]
	,[SUPPLIER_NAME]
	,CONVERT(INT,REPLACE(REPLACE(REPLACE(REPLACE(REPLACE([ORDER_LINE_AMOUNT],',',''),'o','0'),'s','5'),'S','5'),'I','1'))  [ORDER_LINE_AMOUNT] 
	FROM [MSBI].[dbo].[XXBCM_ORDER_MGT]
	WHERE [ORDER_DATE] NOT  LIKE '%[A-Z]%'	
	) A
	LEFT JOIN [MSBI].[dbo].[Supplier] S ON S.[SUPPLIER_NAME]=A.[SUPPLIER_NAME]
	END
	------------------------------------------------------------------------
	EXEC dbo.Insert_Invoices
	EXEC dbo.Insert_Supplier
	EXEC dbo.Insert_Orders


--	Q4. The owner wishes to have a report displaying a summary of Orders with their corresponding list of 
--distinct invoices and their total amount to be able to reconcile his orders and payments. The report 
--shall contain the details as per table below ordered by latest Order Date on top. Implement a Stored 
--Procedure or Function to return the required information.	
	SELECT SUBSTRING(O.[ORDER_REF],5,LEN(O.[ORDER_REF])) [Order Reference],
	LEFT(UPPER(CONVERT(VARCHAR,DATENAME(MM,[ORDER_DATE]))),3)+ '-'+CONVERT(VARCHAR,YEAR([ORDER_DATE])) [Order Date],
	LEFT(S.[SUPPLIER_NAME],1)+'' +LOWER(SUBSTRING(S.[SUPPLIER_NAME],2,LEN(S.[SUPPLIER_NAME]))) [Supplier Name],
	Format([ORDER_TOTAL_AMOUNT],'##,##0.00') [Order Total Amount],
	[ORDER_STATUS] [Order Status],I.[INVOICE_REFERENCE] [Invoice Reference],
	Format(B.[INVOICE_TOTAL_AMOUNT],'##,##0.00')  [Invoice Total Amount],
	CASE WHEN I.[INVOICE_STATUS] ='Paid' THEN 'OK' 
		 WHEN I.[INVOICE_STATUS] ='Pending' THEN 'To follow up'
		 WHEN I.[INVOICE_STATUS] ='' THEN 'Verify' 
		 else '' end AS Action
	FROM [dbo].[Orders] O 
	LEFT JOIN [dbo].[Invoice] I ON I.[REFF_NO_SK]=O.[REFF_NO_SK]
	LEFT JOIN [dbo].[Supplier] S ON S.[SUPPLIER_NAME]=O.[SUPPLIER_NAME]
	LEFT JOIN 
