	SELECT SUBSTRING([INVOICE_REFERENCE],5,5) INV_REF,SUM([INVOICE_AMOUNT]) [INVOICE_TOTAL_AMOUNT] FROM [MSBI].[dbo].[Invoice]
	GROUP BY SUBSTRING([INVOICE_REFERENCE],5,5)
	) B ON O.[ORDER_REF]=B.INV_REF--	Return details for the SECOND (2nd) highest Order Total Amount from the list. Only one record is 
--expected with the following information. Implement a Stored Procedure or Function to return the 
--required information.SELECT SUBSTRING(B.[ORDER_REF],5,LEN(B.[ORDER_REF]))  AS [Order Reference],
       CONVERT(VARCHAR,B.[ORDER_DATE],109) [Order Date],
	  UPPER( B.[SUPPLIER_NAME]) [Supplier Name],
      Format(B.[ORDER_TOTAL_AMOUNT], '##,##0.00') [Order Total Amount],
	   [ORDER_STATUS] [Order Status],
	   I.[INVOICE_REFERENCE] AS [Invoice References]
FROM (
SELECT * FROM (
SELECT [ORDER_REF],[ORDER_DATE],[ORDER_TOTAL_AMOUNT] ,[ORDER_DESCRIPTION],[ORDER_STATUS],[ORDER_LINE_AMOUNT],[SUPPLIER_NAME]
